INSERT INTO `wp_comments` VALUES (1,1,'Một người bình luận WordPress','wapuu@wordpress.example','https://vi.wordpress.org/','','2025-05-23 09:32:23','2025-05-23 09:32:23','Xin chào, đây là một bình luận.\nĐể bắt đầu kiểm duyệt, chỉnh sửa và xóa nhận xét, vui lòng truy cập màn hình Nhận xét trong trang quản trị.\nHình đại diện của người bình luận đến từ <a href=\"https://gravatar.com/\">Gravatar</a>.',0,'1','','comment',0,0);
